                                await SlowModeMessage(e, users.Find(m => m.user == e.Author as SocketGuildUser && m.channel == e.Channel as SocketTextChannel).second, e.Author as SocketGuildUser, e.Channel as SocketTextChannel);
                            }

                        }
                    }
                }


            }
        }

    }
}


